INSERT INTO `brief_infor` VALUES ('1','2018-01-11 17:45:18','2018-01-15 17:14:49','中建材信云智联科技有限公司（以下简称：信云智联）是中建材信息打造的企业数字化转型服务提供商，更是中国企业级云服务领先服务平台。它致力于为企业客户提供便捷、易用、安全、可靠、高性价比的云计算平台及云应用解决方案与服务，专注于行业信息化应用与解决方案。目前，信云智联整合众多国内外优质厂商及行业专家资源，为用户和企业提供国内领军级云服务产品。它以信息化基础设施（数据中心、网络、云）咨询、设计、实施、运维的综合能力为基础，软件开发与服务为核心，企业信息化咨询、解决方案、实施与运维为特色，目标是帮助传统企业快速实现数字化转型。','信云智联公司','0'), ('2','2018-01-22 15:19:08',NULL,'2018年度庆典','总监经理会','2');
